
const slides=document.querySelectorAll(".slide");const maxSlide=slides.length-1;const slidesreversed=[];let i=maxSlide;let slidesposition=[];while(i>=0){slidesreversed.push(slides[i])
slidesposition.push(-100*(i))
i--}
slidesreversed.forEach((slide,indx)=>{slide.style.transform=`translateX(${indx * -100}%)`;});const nextSlide=document.querySelector(".slider-next");let curSlide=slides.length*2-1;nextSlide.addEventListener("click",function(){if(curSlide===slides.length*2-1){return;}else{curSlide++;}
slides.forEach((slide,indx)=>{slidesposition[indx]=slidesposition[indx]-50
slide.style.transform=`translateX(${slidesposition[indx]}%)`;});});const prevSlide=document.querySelector(".slider-prev");prevSlide.addEventListener("click",function(){if(curSlide===1){return;}else{curSlide--;}
slides.forEach((slide,indx)=>{slidesposition[indx]=slidesposition[indx]+50
slide.style.transform=`translateX(${slidesposition[indx]}%)`;});});