
const slides=document.querySelectorAll(".slide");const maxSlide=slides.length-1;const slidesreversed=[];let i=maxSlide;let slidesposition=[];while(i>=0){slidesreversed.push(slides[i])
slidesposition.push(-100*(i))
i--}
slidesreversed.forEach((slide,indx)=>{slide.style.transform=`translateX(${indx * -100}%)`;});const nextSlide=$("div.slider-next");const nextSlidesvg=$("svg.slider-next");const prevSlide=$("div.slider-prev");const prevSlidesvg=$("svg.slider-prev");const svgTransform=0.6
let curSlide=slides.length*2-1;nextSlide.on("click",function(){if(curSlide===slides.length*2-1){return;}else{curSlide++;}
if(curSlide===slides.length*2-1){nextSlidesvg.removeClass('active');}
prevSlidesvg.addClass('active');nextSlidesvg.css('transform',`scale(${-svgTransform}, ${svgTransform})`)
setTimeout(()=>{nextSlidesvg.css('transform','scale(-1, 1)')},200);slides.forEach((slide,indx)=>{slidesposition[indx]=slidesposition[indx]-50
slide.style.transform=`translateX(${slidesposition[indx]}%)`;});});prevSlide.on("click",function(){if(curSlide===1){return;}else{curSlide--;}
if(curSlide===1)prevSlidesvg.removeClass('active');$('#buttontext').css('opacity','0');$('#buttontext').css('transform','scale(0.5)');nextSlidesvg.addClass('active');prevSlidesvg.css('transform',`scale(${svgTransform})`)
setTimeout(()=>{$('#buttontext').css('display','none');},500);setTimeout(()=>{prevSlidesvg.css('transform','scale(1)')},200);slides.forEach((slide,indx)=>{slidesposition[indx]=slidesposition[indx]+50
slide.style.transform=`translateX(${slidesposition[indx]}%)`;});});